import React from 'react';
import { useState } from 'react';
import { Link } from 'react-router-dom';
import { useSpring, animated } from 'react-spring';
import { useDrag } from 'react-use-gesture';

import managerImage from '../SVG/manager_1.svg'; 
import waiterImage from '../SVG/waiter1.svg'; 
import waiterImage2 from '../SVG/waiter2.svg'; 
import tableImage from '../SVG/table.svg';

const Restaurant = () => {
    const handleDoRounds = () => {
        // Create and send your JSON request for doing rounds.
        // Example:
        // fetch('https://api.example.com/do-rounds', {
        //   method: 'POST',
        //   headers: {
        //     'Content-Type': 'application/json',
        //   },
        //   body: JSON.stringify(yourDoRoundsData),
        // })
        //   .then(response => response.json())
        //   .then(data => {
        //     console.log(data);
        //   })
        //   .catch(error => {
        //     console.log(error);
        //   });
    };

    const handleBuyIngredients = () => {
        // Create and send your JSON request for buying more ingredients.
        // Example:
        // fetch('https://api.example.com/buy-ingredients', {
        //   method: 'POST',
        //   headers: {
        //     'Content-Type': 'application/json',
        //   },
        //   body: JSON.stringify(yourBuyIngredientsData),
        // })
        //   .then(response => response.json())
        //   .then(data => {
        //     console.log(data);
        //   })
        //   .catch(error => {
        //     console.log(error);
        //   });
    };

    const handleSendWaiter = () => {
        // Create and send your JSON request for sending a waiter to a table.
        // Example:
        // fetch('https://api.example.com/send-waiter', {
        //   method: 'POST',
        //   headers: {
        //     'Content-Type': 'application/json',
        //   },
        //   body: JSON.stringify(yourSendWaiterData),
        // })
        //   .then(response => response.json())
        //   .then(data => {
        //     console.log(data);
        //   })
        //   .catch(error => {
        //     console.log(error);
        //   });
    };

    const [roundsHovered, setRoundsHovered] = useState(false);
    // const [buyMoreHovered, setBuyMoreHovered] = useState(false);
    const [sendWaiterHovered, setSendWaiterHovered] = useState(false);

    //initial positions for waiters
    const initialWaiterPosition = { x: 0, y: 150 }; // Adjust the 'y' value as needed
    const initialWaiterPosition2 = { x: 100, y: 150 }; // Adjust the 'y' value as needed

    //Create spring animations for waiters
    const waiterSpring = useSpring({ from: initialWaiterPosition, to: initialWaiterPosition });
    const waiterSpring2 = useSpring({ from: initialWaiterPosition2, to: initialWaiterPosition2 });

    //Define the drag handlers for waiters
    const bindWaiter = useDrag(
        (props) => {
            waiterSpring.start(props);
        },
        {
            initial: () => waiterSpring.get(),
        }
    );

    const bindWaiter2 = useDrag(
        (props) => {
            waiterSpring2.start(props);
        },
        {
            initial: () => waiterSpring2.get(),
        }
    );

  return (
  <div className="container" style={containerStyle}>
      <div className="row mt-5">
          <div className='col-3 '>
              <img src={managerImage} alt="Manager" style={imageStyle} /> 
          </div>
          <div className='col-9'> 
          {/* this is the waiter section */}
            <animated.img
                {...bindWaiter()}
                src={waiterImage}
                alt="Waiter"
                style={{
                    ...imageStyle3,
                    ...waiterSpring,
                    // position: 'absolute', 
                }}
            />
            <animated.img
                {...bindWaiter2()}
                src={waiterImage2}
                alt="Waiter"
                style={{
                    ...imageStyle3,
                    ...waiterSpring2,
                    position: 'absolute', 
                }}
            />
            {/* this is the table section below */}
            <img className="col-3" src={tableImage} alt="Table" style={imageStyle2} />
            <img className="col-3" src={tableImage} alt="Table" style={imageStyle2} />
            <img className="col-3" src={tableImage} alt="Table" style={imageStyle2} />
            <img className="col-3" src={tableImage} alt="Table" style={imageStyle2} />
            <img className="col-3 mt-5" src={tableImage} alt="Table" style={imageStyle2} />
            <img className="col-3 mt-5" src={tableImage} alt="Table" style={imageStyle2} />
            <img className="col-3 mt-5" src={tableImage} alt="Table" style={imageStyle2} />
            <img className="col-3 mt-5" src={tableImage} alt="Table" style={imageStyle2} />
          </div>
          <div className='row justify-content-center col-12 mt-5'>
              <button className="col-4 mt-5 mr-2 button-64 " onClick={handleDoRounds} style={buttonStyle} onMouseEnter={() => setRoundsHovered(true)} onMouseLeave={() => setRoundsHovered(false)}>
                <span style={{
                    ...spanStyle,
                    background: roundsHovered ? 'none' : spanStyle.backgroundColor,
                    }}>Do Rounds</span>
              </button>
              {/* <button className="col-4 mt-5 mr-2 button-64 " style={buttonStyle} onMouseEnter={() => setBuyMoreHovered(true)} onMouseLeave={() => setBuyMoreHovered(false)} onClick={handleBuyIngredients}>
                <span style={{
                    ...spanStyle,
                    background: buyMoreHovered ? 'none' : spanStyle.backgroundColor,
                    }}>Buy More Ingredients</span>
              </button> */}
              <button className="col-4 mt-5 button-64 " style={buttonStyle} onMouseEnter={() => setSendWaiterHovered(true)} onMouseLeave={() => setSendWaiterHovered(false)} onClick={handleSendWaiter}>
                <span style={{
                    ...spanStyle,
                    background: sendWaiterHovered ? 'none' : spanStyle.backgroundColor,
                    }}>Send Waiter to Table</span>
              </button>
          </div>
      </div>

      <div className="row " style={footerSec}>
          <div className="col-2 mt-1 mx-2 justify-content-center">
              <p>some picture...</p>
          </div>

          <button className="col-2 mt-1 mx-2 justify-content-center" style={buttonStyle}>
              <a href="https://github.com/Gini24mp/Kitchen-of-Secrets">GitHub</a>
          </button>  

          <button className="col-2 mt-1 mx-2 justify-content-center" style={buttonStyle}>
              <a href="">Documentation</a>
              {/* this would be a link to doxygen */}
          </button> 
      </div>                
    </div>
  );

}


const containerStyle = {
  display: 'flex',
  flexDirection: 'row',
  alignItems: 'center',
  justifyContent: 'center',
  // height: '100vh',
  width: '100vw',
  padding: '10px',
  fontFamily: 'Roboto',
};

const imageStyle = {
  width: '150px', 
};

const imageStyle2 = {
  width: '600px', 
  marginleft: '50%',
};

const imageStyle3 = {
    width: '90px', 
    marginleft: '40%',
};

const buttonStyle = {
  alignItems: 'center',
  backgroundImage: 'linear-gradient(144deg, #AF40FF, #5B42F3 50%, #00DDEB)',
  border: '0',
  borderRadius: '8px',
  boxShadow: 'rgba(151, 65, 252, 0.2) 0 15px 30px -5px',
  boxSizing: 'border-box',
  color: '#FFFFFF',
  display: 'flex',
  fontFamily: 'Phantomsans, sans-serif',
  fontSize: '20px',
  justifyContent: 'center',
  lineHeight: '1em',
  maxWidth: '100%',
  minWidth: '140px',
  padding: '3px',
  textDecoration: 'none',
  userSelect: 'none',
  WebkitUserSelect: 'none',
  touchAction: 'manipulation',
  whiteSpace: 'nowrap',
  cursor: 'pointer',
};

const spanStyle = {
  backgroundColor: 'rgb(5, 6, 45)',
  padding: '16px 24px',
  borderRadius: '6px',
  width: '100%',
  height: '100%',
  transition: 'background 300ms', 
};

const footerSec = {
  position: 'absolute',
  bottom: '0',
  width: '100%',
  height: '60px',
  // backgroundColor: '#f5f5f5',
  textAlign: 'center',
  display: 'flex',
  flexDirection: 'row',
  alignItems: 'center',
  justifyContent: 'center',
  fontSize: '12px',
  color: 'white',
}


export default Restaurant;
