        //     console.log(error);
        //   });
      };

    return (
        <div style={containerStyle}>
            <div className="row mt-5">
                <div className='col-3 '>
                    <img src={customerImage} alt="Customer" style={imageStyle} />
                </div>
                <div className='col-9 '>
                    <img src={tableImage} alt="Table" style={imageStyle2} />
                </div>
            {/* </div> */}
            {/* <div className="row "> */}
                <button className="col-5 mt-5 mx-2 button-64 justify-content-center" style={buttonStyle} onClick={handleSendOrder} onMouseEnter={handleMouseEnter}
                onMouseLeave={handleMouseLeave}>
                    <span style={{
                    ...spanStyle,
                    background: hovered ? 'none' : spanStyle.backgroundColor,
                    }}>Send Order</span>   
                </button>

                <button className="col-5 mt-5 button-64 justify-content-center" style={buttonStyle} onClick={handlePayOrder}>
                    Pay Order
                </button>
            </div>

            <div className="row " style={footerSec}>
                <div className="col-2 mt-1 mx-2 justify-content-center">
                    <p>some picture...</p>
                </div>

                <button className="col-2 mt-1 mx-2 justify-content-center" style={buttonStyle}>
                    <a href="https://github.com/Gini24mp/Kitchen-of-Secrets">GitHub</a>
                </button>  

                <button className="col-2 mt-1 mx-2 justify-content-center" style={buttonStyle}>
                    <a href="">Documentation</a>
                    {/* this would be a link to doxygen */}
                </button> 
            </div>

        </div>
    );


}

